USE toursight_database;

-- CREATE TABEL
CREATE TABLE IF NOT EXISTS `users` (
`id` INTEGER (12) NOT NULL AUTO_INCREMENT, 
`username` VARCHAR(20) UNIQUE, 
`email` VARCHAR(100) UNIQUE, 
`password` VARCHAR(100), 
`refresh_token` TEXT, 
`createdAt` DATETIME NOT NULL, 
`updatedAt` DATETIME NOT NULL, 
PRIMARY KEY (`id`)) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `toursight` (
`id` INTEGER NOT NULL auto_increment , 
`name` VARCHAR(100), 
`city` VARCHAR(200), 
`location` TEXT, 
`category` VARCHAR(50), 
`image` VARCHAR(255), 
`deskripsi` TEXT, 
`history` TEXT, 
PRIMARY KEY (`id`)) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `users_image` (
`id` INTEGER NOT NULL auto_increment , 
`uploadedBy` VARCHAR(200) NOT NULL, 
`image` VARCHAR(300) NOT NULL, 
`createdAt` DATE, 
`updatedAt` DATE, 
PRIMARY KEY (`id`), 
FOREIGN KEY (`uploadedBy`) REFERENCES `users` (`username`)) ENGINE=InnoDB;

